const initialState = {
    user:{
        isLogin: false,
        user: null,
    },
    cart:{
        cartList: [],
    }
}
export const LOG_IN = 'LOG_IN';

export const login = (data) =>{
    type: LOG_IN,
    data
}

const rootReducer = (state=initialState, action) => {
    switch(action.type){
        case LOG_IN:
            return{
                ...state,
                user:{
                    ...state.user,
                    isLogin: true,
                    user:action.data
                }
            }
    }
}

export default rootReducer;
