// 메뉴 전체 레이아웃
import React from 'react'
import PropTypes from 'prop-types'
import { Layout, Menu, Breadcrumb, Space } from 'antd';
import { UnorderedListOutlined, HomeOutlined, ShoppingCartOutlined} from '@ant-design/icons';
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

import Link from 'next/link';
import MenuHeader from './MenuHeader';

const MenuLayout = ({ children }) => {
    const routerMenu = (e) =>{
        console.log(e);
    }
    return (
        <Layout>
            <MenuHeader />
        <Layout>
          <Sider width={200} className="site-layout-background">
            <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              defaultOpenKeys={['sub1']}
              style={{ height: '100%', borderRight: 0 }}
              onClick={routerMenu}
            >
                <Menu.Item key="1" icon={<HomeOutlined />}>
                <Link href ="/">Home</Link>
                </Menu.Item>
                <Menu.Item key="2" icon={<ShoppingCartOutlined />}>
                <Link href ="/cart">My Cart</Link>
                </Menu.Item>
                <SubMenu key="sub1" icon={<UnorderedListOutlined />} title="Product">
                    <Menu.Item key="product"><Link href ="/product">All</Link></Menu.Item>
                    <Menu.Item key="product/men">Men</Menu.Item>
                    <Menu.Item key="product/women">Women</Menu.Item>
                    <Menu.Item key="product/jewelery">jewelery</Menu.Item>
                    <Menu.Item key="product/electronics">electronics</Menu.Item>
                </SubMenu>
            </Menu>
          </Sider>

          <Layout style={{ padding: '0 24px 24px' }}>
            <Content
              className="site-layout-background"
              style={{
                padding: 24,
                margin: 0,
                minHeight: '100vh',
              }}
            >
              {children}
            </Content>
          </Layout>
        </Layout>
      </Layout>
    )
}

MenuLayout.propTypes = {
    children: PropTypes.node.isRequired,
}
export default MenuLayout;
