const initialState = {
    user:{
        isLogin: false,
        user: null,
    },
    cart:{
        cartList: [],
    }
}
export const LOG_IN = 'LOG_IN';
export const LOG_OUT = 'LOG_OUT';

export const loginAction = (data) =>{
    type: LOG_IN,
    data
}

export const logoutAction = (data) =>{
    type: LOG_OUT,
    data
}

const rootReducer = (state=initialState, action) => {
    switch(action.type){
        case LOG_IN:
            return{
                ...state,
                user:{
                    ...state.user,
                    isLogin: true,
                    user:action.data
                }
            }
        case LOG_OUT:
            return{
                ...state,
                user:{
                    ...state.user,
                    isLogin: false,
                }
            }
    }
}

export default rootReducer;
