import { HYDRATE } from 'next-redux-wrapper';
//HYDRATE 액션임


const initialState = {
    user:{
        isLogin: false,
        user: null,
    },
    cart:{
        cartList: [],
    }
}
export const LOG_IN = 'LOG_IN';
export const LOG_OUT = 'LOG_OUT';

export const loginAction = (data) =>{
    return{
        type: LOG_IN,
        data
    }
}

export const logoutAction = () =>{
    return{
        type: LOG_OUT
    }
}

const rootReducer = (state=initialState, action) => {
    switch(action.type){
        case HYDRATE:
            console.log('HYDRATE', action);
            return { ...state, ...action.payload };
        case LOG_IN:
            return{
                ...state,
                user:{
                    ...state.user,
                    isLogin: true,
                    user:action.data
                }
            }
        case LOG_OUT:
            return{
                ...state,
                user:{
                    ...state.user,
                    isLogin: false,
                    user:null
                }
            };
        default:
            return state;
    }
}

export default rootReducer;
