import React,{useCallback, useState} from 'react'

import { Button, Badge, Popover} from 'antd';
import { ShoppingCartOutlined} from '@ant-design/icons';

import Link from 'next/link';

import { useDispatch, useSelector} from 'react-redux';
import { logoutRequestAction } from '../reducers/user';

const MenuHeader = () =>  {

  const dispatch = useDispatch();
  const logout = useCallback(() =>{
      dispatch(logoutRequestAction());
    //Home으로 이동시키기
  },[]);
  const { logoutLoading } = useSelector((state) => state.user)
  const { Order }  = useSelector(state => state.cart);
  
  const cartNum = Order? Order.length: 0;

  const content = (
    <div>
      <p>Content</p>
      <p>Content</p>
    </div>
  );

  return(
    <>
    <Badge count={cartNum}>
    <Popover placement="bottom" title='장바구니' content={content} trigger="click">
      <Button key="2" icon={<ShoppingCartOutlined />}>
      {/* <Link href ="/cart"> */}
        Cart
      {/* </Link> */}
      </Button>
    </Popover>
    </Badge>
    
    <Button loading = {logoutLoading} key="1" onClick={logout}>
      Logout
    </Button>
    </>
  )
}

export default MenuHeader;
