import React, { useCallback, useState, useEffect} from 'react'
import { List, Button, Space,  Row, Col, Input, Menu} from 'antd'
import { DollarCircleOutlined, HeartOutlined, HeartTwoTone, ShoppingCartOutlined , UnorderedListOutlined } from '@ant-design/icons';

import { useSelector, useDispatch, shallowEqual} from 'react-redux';
import { LoadProductAction } from '../reducers/product';
import { addCartAction } from '../reducers/cart';
const { Search } = Input;
const { SubMenu } = Menu;
const ProductList = () => {
  
  const [search, setSearch] = useState("");
  const { Products, loadProductLoading }  = useSelector(state => state.product);
  const dispatch = useDispatch();
  const { me } = useSelector(state => state.user);

  const LoadProduct = useCallback(() =>{
    dispatch(LoadProductAction());
  },[]);


  const addCart = useCallback((item) =>{
    console.log('item',item)
    dispatch(addCartAction(item));
  },[]);

  useEffect(() => {
    LoadProduct();
  }, []);

    const IconText = ({ icon, text }) => (
      <Space>
        {React.createElement(icon)}
        {text}
      </Space>
    );
  //   useEffect(() => {
  //     let row= [];
  //     Order.forEach((data)=>{
  //         if(data.title.toLocaleUpperCase().includes(search.toLocaleUpperCase())){
  //             row.push(data); 
  //             setProducts(row);
  //         }else{
  //             return;
  //         }
  //         //배열에 추가
         
  //     });
  //     if(search === ""){
  //         productService.getProducts().then(data => setProducts(data));
  //     }
  //     row= [];
  //     console.log(row);

  // }, [search]);
    const onSearch = () =>{
      LoadProduct();
    }
    const style = { background: '#fff', padding: '8px 20px',marginBottom: '60px'};
    return (
      <>
        <Row gutter ={[48, 48]} justify="center">
        <Col span = {6}>
        <Menu
              mode="inline"
              defaultOpenKeys={['sub1']}
              style={{ borderRight: 0 }}
              selectable ={false}
            >
                <Menu.Item key="1">
                <Search placeholder="input search text" onSearch={onSearch} enterButton />
                </Menu.Item>
              <SubMenu key="sub1" icon={<UnorderedListOutlined />} title="Product">
                    <Menu.Item key="3">All</Menu.Item>
                    <Menu.Item key="4">Mens</Menu.Item>
                    <Menu.Item key="5">Womens</Menu.Item>
                    <Menu.Item key="6">jewelery</Menu.Item>
                    <Menu.Item key="7">electronics</Menu.Item>
                </SubMenu>
              </Menu>
        </Col> 
       
        <Col span = {18}>
       
        <List   style={style}
                itemLayout="vertical"
                size="large"
                pagination={{
                onChange: page => {
                    console.log(page);
                },
                pageSize: 5,
                }}
                loading = {loadProductLoading}
                dataSource={Products}
                renderItem={item => (
                <List.Item
                    key={item.id}
                    actions={[
                    <IconText icon={DollarCircleOutlined} text={item.price} key="list-vertical-star-o" />,
                    me&& <Button icon={<ShoppingCartOutlined />} onClick={() =>addCart(item)}>
                    </Button>,
                    ]}
                    extra={
                    <img
                        width={150}
                        height ={200}
                        alt="logo"
                        src={item.image}
                    />
                    }
                >
                    <List.Item.Meta
                    title={<a href={item.href}>{item.title}</a>}
                    description={item.description}
                    />
                    {item.content}
                </List.Item>
            )}
        />
          </Col>
        </Row>
        </>
    )
}
export default ProductList
