import React from 'react'
import { List, Typography, Divider, Row, Col, Button } from 'antd';
import { MinusCircleOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';

const OrderList = () => {

    const Products  = useSelector(state => state.cart.Products.length !== 0 && Products);
    console.log(Products);

    const orderListFooter = () =>{
        return(
            <>
            <Divider></Divider>
            <Row justify="space-between">
                 <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 4 }}>
                 총 금액: $166
                </Col>
                <Col xs={{ span: 5, offset: 1 }} lg={{ span: 6, offset: 4}}>
                <Button>주문하기</Button>
                </Col>
            </Row>
            </>
        )
    }
    return (
        <>
        <Divider orientation="middle">주문서</Divider>
        <Row justify="center">
            <Col span = {16} >
                <List
                header={<div>주문서</div>}
                footer={orderListFooter()}
                bordered
                dataSource={Products}
                renderItem={item => (
                    <List.Item key={item.id}>
                    <Col span = {12} >
                    {item.title}
                    </Col>
                    <Col span = {2} >
                    ${item.price}
                    </Col>
                    <Col span = {2} >
                    <Button shape="circle" icon={<MinusCircleOutlined />} />
                    </Col>
                    </List.Item>
                )}
                />
        </Col>
         </Row>
        </>
    )
}

export default OrderList
